import { useEffect, useState } from "react";

import { ClockIcon } from "@heroicons/react/outline";

// Custom Auth Hook
import useAuth from "../../hooks/useAuth";

// Api Import
import DiscussionTopicApi from "../../api/DiscussionTopicApi";

import parse from "html-react-parser";

const HistorySection = () => {
	const { authInfo } = useAuth();

	// Refresh Loads
	const [refresh, setRefresh] = useState(0);
	const { discussionState, discussionIndex } = DiscussionTopicApi();
	useEffect(() => {
		discussionIndex();
	}, [refresh]);

	const fillerPost = discussionState.filter((post, count) => {
		if (count < 3 && post.user_id == authInfo.id) {
			count++;
			return true;
		}
		return false;
	});

	return (
		<div className=" sticky top-0 h-[95vh] bg-white pr-4 sm:h-full sm:pr-6 lg:flex-shrink-0 lg:border-l lg:border-gray-200 lg:pr-8 xl:pr-0">
			<div className="pl-6 lg:w-80">
				<div className="flex justify-between border-b border-gray-200 pt-6 pb-2">
					<h2 className="text-sm font-semibold">Discussion Activity</h2>
					<ClockIcon className="h-5 w-5" />
				</div>
				<div className="bg-white pr-4 sm:pr-6 lg:flex-shrink-0 lg:pr-8 xl:pr-0">
					<div className="my-2 h-[28rem] overflow-y-auto scrollbar-hide">
						{fillerPost.map((innerPost) => {
							return (
								<div key={innerPost.id}>
									<div className="flex w-full space-y-3 border-b border-gray-300 py-4">
										<div className="h-12 w-12 flex-shrink-0">
											<img
												className="h-10 w-10 rounded-full"
												src="data:image/jpeg;base64,"
												alt=""
											/>
										</div>
										<div className="ml-2 flex flex-grow flex-col">
											<div className="flex text-sm">
												<span className="font-semibold">{innerPost.name}</span>
											</div>
											<p className="mt-1 text-sm">{parse(innerPost.content)}</p>
										</div>
									</div>
								</div>
							);
						})}
					</div>
				</div>
			</div>
		</div>
	);
};

export default HistorySection;
