import React from "react";
import parse from "html-react-parser";

const Comment = ({ detail }) => {
	return (
		<>
			{detail.comments.map((comments) => (
				<div key={comments.id} className="flex">
					<div className="mr-3 flex-shrink-0">
						<img
							className="mt-2 h-6 w-6 rounded-full sm:h-8 sm:w-8"
							src={
								comments.user.username
									? `https://ui-avatars.com/api/?background=random&color=fff&name=${comments.user.username}`
									: " data:image/jpeg;base64,"
							}
							alt=""
						/>
					</div>
					<div className="flex-1 rounded-lg border px-4 py-2 leading-relaxed sm:px-6 sm:py-4">
						<div className="mb-2 flex justify-between text-sm">
							<strong>{comments.user.username}</strong>{" "}
							<span className="text-xs text-gray-400">
								{new Date(comments.created_at).toLocaleDateString("Jamaica", {
									month: "long",
									day: "2-digit",
									year: "2-digit",
								})}
							</span>
						</div>

						<p className="text-sm">{parse(comments.content)}</p>

						<div className="mt-4 flex items-center">
							{/* <div className="text-sm font-semibold text-gray-500">
								5 Replies
							</div> */}
						</div>
					</div>
				</div>
			))}
		</>
	);
};

export default Comment;
